mapper.insert:
INSERT INTO attendance (
    user_idx, result
) VALUES (
    #{userIdx}, #{result}
)

mapper.select:
SELECT * FROM
    luxury_storyteller.attendance
WHERE user_idx = #{userIdx}
AND DATE(created_at) = #{createdAt};

mapper.update:
UPDATE
    attendance
SET
    result = #{result}
WHERE
    attendance_idx = #{attendanceIdx}
